
define('text!widgets/table/CheckboxHeaderCell/CheckboxHeaderCell.html',[],function () { return '<th style="text-align:center">\n\t<input class="ebCheckbox" type="checkbox" value="1" /> \n\t<span class="ebCheckbox-inputStatus"></span> \n</th>';});

define('widgets/table/CheckboxHeaderCell/CheckboxHeaderCellView',[
	"jscore/core",
	"text!./CheckboxHeaderCell.html"
], function(core, template){
	
	return core.View.extend({

		getTemplate: function() {
			return template;
		},

		getCheckbox: function() {
			return this.getElement().find(".ebCheckbox");
		}

	});
	

});
/**
*	Provides a header cell with a checkbox. When the checkbox is checked, all of the checkboxes in the same column will be checked. This cell should be used in conjunction with the CheckboxCell.
*
*	@class table.CheckboxHeaderCell
*/
define('widgets/table/CheckboxHeaderCell/CheckboxHeaderCell',[
	"widgets/table/Cell",
	"./CheckboxHeaderCellView"
], function(Cell, View) {

	var CheckboxHeaderCell = Cell.extend({

		View: View,

		onCellReady: function() {

			this.getEventBus().subscribe("CheckboxHeaderCell:uncheck-header", function() {
				this.view.getCheckbox().setProperty("checked", false);
			}.bind(this));

			this.view.getCheckbox().addEventHandler("click", function() {
				var checked = this.view.getCheckbox().getProperty("checked");
				this.getEventBus().publish("CheckboxCell:check", checked);
			}.bind(this));
		}

	});

	return CheckboxHeaderCell;

});
define('widgets/table/CheckboxHeaderCell', ['widgets/table/CheckboxHeaderCell/CheckboxHeaderCell'], function (main) { return main; });
